INSERT INTO `#__jshopping_payment_method` (`payment_code`, `scriptname`, `payment_class`, `payment_publish`, `payment_ordering`, `payment_params`, `payment_type`, `price`, `price_type`, `tax_id`, `image`, `show_descr_in_email`, `show_bank_in_order`, `order_description`, `name_en-GB`, `description_en-GB`, `name_de-DE`, `description_de-DE`, `name_ru-RU`, `description_ru-RU`) VALUES ('HutkiGrosh', 'pm_hg', 'pm_hg', 1, 0, '', 2, 0.00, 0, 1, '', 0, 1, '', 'HutkiGrosh', 'Pay by ERIP', 'HutkiGrosh', 'Pay by ERIP', 'HutkiGrosh', '«Хуткi Грош»™ — платежный сервис по выставлению счетов в АИС "Расчет" (ЕРИП).\r\n После выставления счета Вам будет доступна его оплата пластиковой карточкой и электронными деньгами, в любом из отделений банков, кассах, банкоматах, платежных терминалах, в системе электронных денег, через Интернет-банкинг, М-банкинг, интернет-эквайринг');
INSERT INTO `#__jshopping_config_statictext` (`alias`, `use_for_return_policy`, `text_en-GB`, `text_ru-RU`) VALUES ('order_hg_completion_text', 0, '', '<p>Счет №<strong>@order_number</strong> успешно выставлен в ЕРИП</p>\r\n<p>Вы можете оплатить его наличными деньгами, пластиковой карточкой и электронными деньгами, в любом из отделений банков, кассах, банкоматах, платежных терминалах, в системе электронных денег, через Интернет-банкинг, М-банкинг, интернет-эквайринг</p>\r\n<p>Для оплаты счета в ЕРИП необходимо:</p>\r\n<ol>\r\n<li>Выберите дерево платежей ЕРИП</li>\r\n<li>Выберите услугу: <strong>Платежи &gt; Интернет магазины</strong></li>\r\n<li>Введите номер счета <strong>@order_number</strong></li>\r\n<li>Проверить корректность информации</li>\r\n<li>Совершить платеж.</li>\r\n</ol>');